/**
 * Maximum allowed character level.
 * @type {number}
 */
DND5E.maxLevel = 20;


// Initialization hook to register settings and set initial values
Hooks.once('init', () => {
  // Register custom settings
  game.settings.register('dnd5e', 'maxAbilityScore', {
    name: 'Maximum Ability Score',
    hint: 'The maximum ability score allowed by default.',
    scope: 'world',
    config: true,
    type: Number,
    default: 20
  });

  // Initialize the global value
  updateMaxAbilityScore();
});

// Function to update the global maxAbilityScore
function updateMaxAbilityScore() {
  DND5E.maxAbilityScore = game.settings.get('dnd5e', 'maxAbilityScore');
}

// Hook to handle setting updates
Hooks.on('updateSetting', (setting, value) => {
  if (setting === 'your-system-name.maxAbilityScore') {
    updateMaxAbilityScore();
  }
});


